func testPurchasesUpdate(t *testing.T) {
	t.Parallel()

	if 0 == len(purchasePrimaryKeyColumns) {
		t.Skip("Skipping table with no primary key columns")
	}
	if len(purchaseAllColumns) == len(purchasePrimaryKeyColumns) {
		t.Skip("Skipping table with only primary key columns")
	}

	seed := randomize.NewSeed()
	var err error
	o := &Purchase{}
	if err = randomize.Struct(seed, o, purchaseDBTypes, true, purchaseColumnsWithDefault...); err != nil {
		t.Errorf("Unable to randomize Purchase struct: %s", err)
	}

	ctx := context.Background()
	tx := MustTx(boil.BeginTx(ctx, nil))
	defer func() { _ = tx.Rollback() }()
	if err = o.Insert(ctx, tx, boil.Infer()); err != nil {
		t.Error(err)
	}

	count, err := Purchases().Count(ctx, tx)
	if err != nil {
		t.Error(err)
	}

	if count != 1 {
		t.Error("want one record, got:", count)
	}

	if err = randomize.Struct(seed, o, purchaseDBTypes, true, purchasePrimaryKeyColumns...); err != nil {
		t.Errorf("Unable to randomize Purchase struct: %s", err)
	}

	if rowsAff, err := o.Update(ctx, tx, boil.Infer()); err != nil {
		t.Error(err)
	} else if rowsAff != 1 {
		t.Error("should only affect one row but affected", rowsAff)
	}
}

func testPurchasesSliceUpdateAll(t *testing.T) {
	t.Parallel()

	if len(purchaseAllColumns) == len(purchasePrimaryKeyColumns) {
		t.Skip("Skipping table with only primary key columns")
	}

	seed := randomize.NewSeed()
	var err error
	o := &Purchase{}
	if err = randomize.Struct(seed, o, purchaseDBTypes, true, purchaseColumnsWithDefault...); err != nil {
		t.Errorf("Unable to randomize Purchase struct: %s", err)
	}

	ctx := context.Background()
	tx := MustTx(boil.BeginTx(ctx, nil))
	defer func() { _ = tx.Rollback() }()
	if err = o.Insert(ctx, tx, boil.Infer()); err != nil {
		t.Error(err)
	}

	count, err := Purchases().Count(ctx, tx)
	if err != nil {
		t.Error(err)
	}

	if count != 1 {
		t.Error("want one record, got:", count)
	}

	if err = randomize.Struct(seed, o, purchaseDBTypes, true, purchasePrimaryKeyColumns...); err != nil {
		t.Errorf("Unable to randomize Purchase struct: %s", err)
	}

	// Remove Primary keys and unique columns from what we plan to update
	var fields []string
	if strmangle.StringSliceMatch(purchaseAllColumns, purchasePrimaryKeyColumns) {
		fields = purchaseAllColumns
	} else {
		fields = strmangle.SetComplement(
			purchaseAllColumns,
			purchasePrimaryKeyColumns,
		)
	}

	value := reflect.Indirect(reflect.ValueOf(o))
	typ := reflect.TypeOf(o).Elem()
	n := typ.NumField()

	updateMap := M{}
	for _, col := range fields {
		for i := 0; i < n; i++ {
			f := typ.Field(i)
			if f.Tag.Get("boil") == col {
				updateMap[col] = value.Field(i).Interface()
			}
		}
	}

	slice := PurchaseSlice{o}
	if rowsAff, err := slice.UpdateAll(ctx, tx, updateMap); err != nil {
		t.Error(err)
	} else if rowsAff != 1 {
		t.Error("wanted one record updated but got", rowsAff)
	}
}

func testPurchasesUpsert(t *testing.T) {
	t.Parallel()

	if len(purchaseAllColumns) == len(purchasePrimaryKeyColumns) {
		t.Skip("Skipping table with only primary key columns")
	}

	seed := randomize.NewSeed()
	var err error
	// Attempt the INSERT side of an UPSERT
	o := Purchase{}
	if err = randomize.Struct(seed, &o, purchaseDBTypes, true); err != nil {
		t.Errorf("Unable to randomize Purchase struct: %s", err)
	}

	ctx := context.Background()
	tx := MustTx(boil.BeginTx(ctx, nil))
	defer func() { _ = tx.Rollback() }()
	if err = o.Upsert(ctx, tx, false, nil, boil.Infer(), boil.Infer()); err != nil {
		t.Errorf("Unable to upsert Purchase: %s", err)
	}

	count, err := Purchases().Count(ctx, tx)
	if err != nil {
		t.Error(err)
	}
	if count != 1 {
		t.Error("want one record, got:", count)
	}

	// Attempt the UPDATE side of an UPSERT
	if err = randomize.Struct(seed, &o, purchaseDBTypes, false, purchasePrimaryKeyColumns...); err != nil {
		t.Errorf("Unable to randomize Purchase struct: %s", err)
	}

	if err = o.Upsert(ctx, tx, true, nil, boil.Infer(), boil.Infer()); err != nil {
		t.Errorf("Unable to upsert Purchase: %s", err)
	}

	count, err = Purchases().Count(ctx, tx)
	if err != nil {
		t.Error(err)
	}
	if count != 1 {
		t.Error("want one record, got:", count)
	}
}
